import React, { useState } from "react";
import "./listedecourse.css";

const Listedecourse = () => {

    const [todolist, setTodoList] = useState([]);
    const [entry, setEntry] = useState("");



    return (
        <div>

            <input type="text" value={entry} onChange={event => { setEntry(event.target.value); }}></input>
            <button onClick={() => { let aux = [...todolist]; aux.push(entry); setTodoList(aux) }}>Add</button>
            {/* on déroule les items du tableau dans un .map comme vu dans l'exercice avec les bags - ne pas oublier le return dans un .map lorsqu'on déroule les elements d'un tableau */}
            {todolist.map((item) => {
                // ici item représente un des objets du tableau, par exemple {name: "faire les courses", done: false}
                return <div className="item"> {item} </div>; // on récupere donc son nom avec la clé name
            })}
        </div>
    )
}
export default Listedecourse;
