import React, { useState } from "react";
import "./listedecourse.css";

const Listedecourse = () => {

    const [todolist, setTodoList] = useState([]);
    const [entry, setEntry] = useState("");
    const [indice, setIndice] = useState(-1);



    return (
        <div>

            <input type="text" value={entry} onChange={event => { setEntry(event.target.value); }}></input>
            <button onClick={() => { let aux = [...todolist]; aux.push(entry); setTodoList(aux); setEntry(""); setIndice(indice + 1); }}>Add</button>

            {
                todolist.map((x, i) => { return <div className="items"><button value={indice} onClick={() => { let aux = [...todolist]; aux.splice(i, 1); setTodoList(aux); return <div className="items"><button value={indice} onClick={() => { let aux = [...todolist]; aux.splice(i, 0); setTodoList(aux); }}>X</button><span className="item">{x}</span></div> }}>X</button><span className="item">{x}</span></div> })
            }
        </div>
    )
}
export default Listedecourse;
